def get_LastName_from_FN(FN_list,Name_list ):
    LN_list = []
    for index,name in enumerate(Name_list):
        if len(name)==2:
            LN_list.append(name[0])
        else:
            FN = FN_list[index]
            LN_list.append(name[:len(name) - len(FN)])
        
    return LN_list
 
